import React from 'react';
import { Link } from 'react-router-dom';
import { FaBars, FaTimes } from 'react-icons/fa';
import '../styles/Navbar.css';

const Navbar = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = React.useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <nav className="navbar">
      <div className="container mx-auto flex justify-between items-center p-4">
        <div className="logo">
          <Link to="/" className="text-2xl font-bold text-white">Matrimony Connect</Link>
        </div>

        {/* Mobile menu icon */}
        <div className="lg:hidden">
          <button onClick={toggleMobileMenu} className="text-white">
            {isMobileMenuOpen ? <FaTimes /> : <FaBars />}
          </button>
        </div>

        {/* Navbar links */}
        <div className={`nav-links ${isMobileMenuOpen ? 'open' : ''}`}>
          <ul className="flex space-x-8 lg:flex-row flex-col items-center">
            <li><Link to="/" className="text-white hover:text-rose-400">Home</Link></li>
            <li><Link to="/about" className="text-white hover:text-rose-400">About Us</Link></li>
            <li><Link to="/contact" className="text-white hover:text-rose-400">Contact</Link></li>
            <li><Link to="/login" className="bg-rose-500 hover:bg-rose-600 text-white font-bold py-2 px-4 rounded-full">Login</Link></li>
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
